/*jshint esversion: 6 */
function handleNode(context) {
    var sprint_name = context.getParam('sprint');
    var sprint_obj = JSON.parse(context.getParam('sprint_obj'));
    var ou = JSON.parse(context.getParam('ou'));
    var sprintMetrics = JSON.parse(context.getParam('sprintMetrics'));
    var hygieneAssignee = JSON.parse(context.getParam('hygieneAssignee'));
    var hygieneStoryPoints = JSON.parse(context.getParam('hygieneStoryPoints'));
    var hygieneMissingAC = JSON.parse(context.getParam('hygieneMissingAC'));
    var hygienePoorDesc = JSON.parse(context.getParam('hygienePoorDesc'));
    var leadTime = JSON.parse(context.getParam('leadTime'));
    var issueStoryPtBD = JSON.parse(context.getParam('issueStoryPtBD'));
    //var committedStoriesCount = JSON.parse(context.getParam('committedStoriesCount'));
    var itemtype_count_sp = JSON.parse(context.getParam('itemtype_count_sp'));
    var unplanned_stories = [];

    var sprintMetricsMap  = sprintMetrics.reduce(function(map, spr) {
    	map[spr.additional_key] = spr;
    	return map;
	}, {});
    //var sprint_name1 = sprint_name.replaceAll("\\","");
	var sprint = sprint_obj[sprint_name];


    var message = "<div style='border: 1px solid; padding: 24px'>";
    message += '<h2>Sprint Report for ' + ou.name + '</h2>';
    message += '<h3>Sprint: ' + sprint_name + '</h3>';

    message += '<h4>Sprint Status</h4>';
    message += '<table border=1 cellpadding=5 style="border-collapse: collapse; width:100%;">';

    var addTableRow = function (vals, isHeader = false) {
        var rowHtml = '<tr>';
        vals.forEach(val => {
            rowHtml += (isHeader ? '<th style="text-align: left;">' : '<td>') + val + (isHeader ? '</th>' : '</td>');
        });
        rowHtml += '</tr>';
        message += rowHtml;
    };
    //var committedStoriesCountNum = committedStoriesCount && committedStoriesCount[0] && committedStoriesCount[0].total_tickets || 0;
    //var headers = ['Committed # of stories', 'Committed Story Points', 'Delivered Story Points', 'Commit to Done', 'Creep Story Points', 'Delivered Creep Story Points'];
  var headers = ['Committed Story Points', 'Delivered Story Points', 'Commit to Done', 'Creep Story Points', 'Delivered Creep Story Points'];
    var commitToDonePc = ((((sprint.delivered_story_points/sprint.committed_story_points))*100).toFixed(2) + "%");
    var vals = [sprint.committed_story_points, sprint.delivered_story_points, commitToDonePc, sprint.creep_story_points, sprint.delivered_creep_story_points];

    addTableRow(headers, true);
    addTableRow(vals);
    message += '</table>';
    message += '<h4></h4>';
    message += '<table border=1 cellpadding=5 style="border-collapse: collapse; width:100%;">';
    var headers2 = ['Total workitems', 'Total unestimated features', 'Total unestimated stories'];
    var vals2 = [sprint.total_workitems?sprint.total_workitems:0, sprint.unestimated_workitems_by_type["Feature"]?sprint.unestimated_workitems_by_type["Feature"]:0, 
                 sprint.unestimated_workitems_by_type["User Story"]? sprint.unestimated_workitems_by_type["User Story"]: 0];

    addTableRow(headers2, true);
    addTableRow(vals2);
    message += '</table>';

    message += '<h4>Hygiene Misses</h4>';
    message += '<table border=1 cellpadding=5 style="border-collapse: collapse; width:100%;">';
    addTableRow(['Tickets without Assignee', 'Stories/Bugs without Story Points', 'Missing Acceptance Criteria', 'Has Poor Description'], true);

    function getHygieneCount(hygieneObject){
      if(hygieneObject.length > 0 && hygieneObject[0].project && hygieneObject[0].project.records && hygieneObject[0].project.records.length > 0 && hygieneObject[0].project.records[0].total_tickets) {
        return hygieneObject[0].project.records[0].total_tickets;
      }
      return 0;
    }


    addTableRow([getHygieneCount(hygieneAssignee), getHygieneCount(hygieneStoryPoints), getHygieneCount(hygieneMissingAC), getHygieneCount(hygienePoorDesc)]);
    message += '</table>';

    message += '<h4>Story Point Breakdown</h4>';
    message += '<table border=1 cellpadding=5 style="border-collapse: collapse; width:100%;">';

    if(itemtype_count_sp && Object.keys(itemtype_count_sp).length > 0) {
      addTableRow(['Ticket Type', 'Total Tickets', 'Story Points'], true);
        Object.keys(itemtype_count_sp).forEach(type => {
           addTableRow([type, itemtype_count_sp[type].total_tickets, itemtype_count_sp[type].total_story_points]);
        });      

    }  
    message += '</table>';

    if (leadTime.length > 0) {
        var roundDays = function (secs) {
            return Math.round(secs / 86400);
        };
        var roundDays2 = function (secs) {
            return Math.round(((secs / 86400) + Number.EPSILON) * 100) / 100;
        };
//        message += '<h3>Lead Time for Stories and Bugs</h3>';
//        message += '<table border=1 cellpadding=5 style="border-collapse: collapse; ">';
//        addTableRow(['', 'Average Time (Days)', 'Median Time (Days)', 'P90 (Days)', 'P95 (Days)'], true);
//        var mean = 0, median = 0, p90 = 0, p95 = 0;
//        leadTime.forEach(lt => {
//            mean += lt.mean;
//            median += lt.median;
//            p90 += lt.p90;
//            p95 += lt.p95;
//        });
//        addTableRow(['Overall Lead Time', roundDays(mean), roundDays(median), roundDays(p90), roundDays(p95)]);
//        message += '</table>';

        message += '<h4>Lead Time Breakdown</h4>';
        message += '<table border=1 cellpadding=5 style="border-collapse: collapse; width:100%;">';
        addTableRow(['Stage', 'Average Time (Days)', 'Median Time (Days)', 'P90 (Days)', 'P95 (Days)', 'Rating'], true);
        leadTime.forEach(lt => {
            addTableRow([lt.key,
            roundDays2(lt.mean),
            roundDays2(lt.median),
            roundDays2(lt.p90),
            roundDays2(lt.p95),
            lt.velocity_stage_result.rating]);
        });
        message += '</table>';
    }

    if(unplanned_stories.length > 0) {
      message += '<h4>Added Unplanned Stories</h4>';
      message += '<table border=1 cellpadding=5 style="border-collapse: collapse; width:100%;">';
      addTableRow(['Key', 'Summary'], true);
      unplanned_stories.forEach(function(story){
        addTableRow([story.key, story.summary]);
      });
      message += '</table>';
    }      


    //message+="<br> <p> Click <a href='https://app.levelops.io/#/admin/dashboards/75?OU="+parseInt(ou.id)+"'>here</a> for more details.</p>";
    message+= "</div>";

    return {
        state: 'success',
        output: {
            'message': message,
          sprint: sprint

        }
    };
}

