function handleNode(context) {
  var sprints = context.getParam('sprints');
  // levelops.serviceName().doSomething() - invoke a levelops service
  var str = "'" + sprints + "'";

  //var sprints_arr = sprints.split("additional_key");  
  var j_s = JSON.stringify(sprints);
  var parsed = JSON.parse(sprints);
  var split = j_s.split("additional_key");

  var split_length = j_s.split("additional_key").length;



  var spr_arr = [];
  var sprint_obj = {};
  if (split_length > 0) {
  	for (var i=1; i< split_length; i++) {
    	var str1 = split[i].split("integration_id")[0].split(":")[1].split(",")[0];

		if(str1.length > 0) {
          var a1 = (str1).slice(2,-2);

          var aa = a1.split("\\\\");
          spr_arr.push(aa.join("\\"));
          sprint_obj[aa.join("\\")] = parsed.records[i-1];
        }
    }

  }


  return { 
    state : 'success',
    output : { 
      //'sprints' : sprints.records,
      //'sprintss' : ["CTP - Core Platform\\\\Global Calendar\\\\Fiscal Year 2023\\\\FY23Q1\\\\FY23Q1.2"],
      //j_p: j_s.split("additional_key")[1].split("integration_id")[0].split(":")[1].split(",")[0],
      //j_s: j_s,
      spr_arr: spr_arr,
      sprint_obj: sprint_obj
    } 
  };
}

