function handleNode(context) {
  var epicDetails = JSON.parse(context.getParam('epic'));
  var issueTypes = JSON.parse(context.getParam('issueTypes'));
  var statuses = JSON.parse(context.getParam('statuses'));
  var epicStoryDetails = JSON.parse(context.getParam('epicDetails')).records;
  var historical_velocity = JSON.parse(context.getParam('historical_velocity'));
  var parameters = context.getParam('parameters');
  var estimate_field = parameters.estimate_field || "";
  var dueDate_field = parameters.due_date_field || "";

  var message = '<h3>Epic Progress Report</h3>';

  var addTableRow = function (vals, isHeader = false) {
    var rowHtml = '<tr>';
    vals.forEach(val => {
      rowHtml += (isHeader ? '<th>' : '<td>') + val + (isHeader ? '</th>' : '</td>');
    });
    rowHtml += '</tr>';
    message += rowHtml;
  };

  var epicStartTime;
  epicDetails.status_list.forEach(function (statusEntry) {
    if (statusEntry.status === "IN PROGRESS") {
      epicStartTime = statusEntry.start_time; // sort to find out multiple InProg events
    }
  });

  var epicEstimateInWeeks, endDateAsPerEstimate = "";
  if(estimate_field) {
    epicEstimateInWeeks = epicDetails.custom_fields[estimate_field] || 0;
    endDateAsPerEstimate = (epicEstimateInWeeks && (epicEstimateInWeeks > 0)) ? (epicStartTime + epicEstimateInWeeks * 7 * 86400) : "";
  }

  var endDateAsPerDueDate = "";
  if(epicDetails.issue_due_at > 0) {
    endDateAsPerDueDate = epicDetails.issue_due_at;
    epicEstimateInWeeks = (epicDetails.issue_due_at - epicStartTime)/(86400*7);
  } else if (dueDate_field.length>0){
    endDateAsPerDueDate = (epicDetails.custom_fields && epicDetails.custom_fields[dueDate_field]) ? (epicDetails.custom_fields[dueDate_field] / 1000).toFixed() : "";
  }

  message += '<table border=1 cellpadding=5 style="border-collapse: collapse; ">';
  var headers = ['Epic Id', 'Epic Name', 'Estimate', 'Start Date', 'Due Date'];
  addTableRow(headers, true);

  var epicStartDate = (new Date(epicStartTime * 1000)).toLocaleDateString();
  var estimateW = epicEstimateInWeeks ? epicEstimateInWeeks.toFixed(0): "Not estimated";
  var epicProjectedEndDate = endDateAsPerEstimate !== "" ? endDateAsPerEstimate : "Not Available";
  var epic_due_date = parseInt(endDateAsPerDueDate) > 0 ? (new Date(endDateAsPerDueDate*1000)).toLocaleDateString(): "Not Available";
  addTableRow([epicDetails.key, epicDetails.summary, estimateW, epicStartDate, epic_due_date]);
  message += '</table>';
  message += '<br>';

  //************************************
  var epicstoryPoints = (epicDetails.story_points ? epicDetails.story_points : 0);
  message += `<p>Total Story Points: ${epicstoryPoints}</p>`;

  message += '<h4>Epic Stats</h4>';
  if (issueTypes && issueTypes.records && issueTypes.records.length > 0) {
    message += '<table border=1 cellpadding=5 style="border-collapse: collapse; ">';
    addTableRow(["Issue Type", "Total Story Points", "Total Tickets", "Total Unestimated Tickets"], true);
    issueTypes.records.forEach(function (issueType) {
      addTableRow([issueType.key, issueType.total_story_points, issueType.total_tickets, issueType.total_unestimated_tickets]);
    });
    message += '</table>';
  }
  if (statuses && statuses.records && statuses.records.length > 0) {
    message += '<br>';
    message += '<table border=1 cellpadding=5 style="border-collapse: collapse; ">';
    addTableRow(["Status", "Total Story Points", "Total Tickets", "Total Unestimated Tickets"], true);
    statuses.records.forEach(function (status) {
      addTableRow([status.key, status.total_story_points, status.total_tickets, status.total_unestimated_tickets]);
    });
    message += '</table>';
  }
  var prioVsStatus = {}
  epicStoryDetails.forEach(function (task) {
    if (!prioVsStatus[task.priority]) {
      prioVsStatus[task.priority] = {};
    }
    task.status_list.forEach(function (stts) {
      if (!prioVsStatus[task.priority][stts.status]) {
        prioVsStatus[task.priority][stts.status] = 0;
      }
      prioVsStatus[task.priority][stts.status] += 1;
    });
  });

  message += '<br>';
  message += '<h4>Epic Statuses</h4>';
  Object.keys(prioVsStatus).forEach(function (prio) {
    message += "<p>" + prio + " Tasks status</p>";
    message += '<table border=1 cellpadding=5 style="border-collapse: collapse; ">';
    addTableRow(Object.keys(prioVsStatus[prio]), true);
    addTableRow(Object.values(prioVsStatus[prio]));
    message += '</table>';
  });

  // when tracking by story points
  var epicSP = 0;
  var epicSPCompleted = 0;
  epicStoryDetails.forEach(function (story) {
    if(story.story_points) {
      epicSP += story.story_points;
      var status = story.status.toLowerCase();
      if (status === "done" || status === "closed") {
        epicSPCompleted += story.story_points;
      }
    }
  });
  var week_elapsed = (((Date.now() / 1000) - epicStartTime) / (86400 * 7));
  var currentVelocity = parseFloat(Math.abs((epicSPCompleted / week_elapsed)).toFixed(2));
  var epicSPRemaining = epicstoryPoints - epicSPCompleted;
  message += "<br> <p> Story points completed: " + epicSPCompleted + " </p>";
  message += "<p> Story points Remaining: " + epicSPRemaining + " </p>";
  if(endDateAsPerEstimate !== "" || endDateAsPerDueDate !== ""){
  	message += "<p> <b>Current Velocity</b>: " + currentVelocity + " Story Points per week</p>";
    var week_remaining = epicEstimateInWeeks > week_elapsed ? epicEstimateInWeeks - week_elapsed : 1
    var epicExpectedVelocity = parseFloat(Math.abs((epicSPRemaining / (week_remaining))).toFixed(2));
    message+= "<p> <b>Expected Velocity</b>: "+ epicExpectedVelocity +" Story Points per week</p>";
  }

  message += "<p> <b>*Historical Velocity</b>: " + historical_velocity + " Story Points per week (Last Quarter)</p>";

  return {
    state: 'success',
    output: {
      'message': message
    }
  };
}

