    var sp_sum = 0, count = 0;

  // velocity in story points per week
    comp_epics.forEach(function (epic) {
        var start, end = 0;
        if (epic.story_points > 0) {
            epic.status_list.forEach(function (status) {
                if (status.status === "IN PROGRESS") {
                    start = status.start_time;
                }
                if (status.status === "DONE") {
                    end = status.start_time;
                }
            });
            var weekspent = (((end - start) / (86400 * 7)) || 1);
            var velocity = ((epic.story_points / weekspent) || 0);
            if (weekspent > 1) {
                sp_sum = sp_sum + velocity;
                count += 1;
            }

        }
    });
   var avg = sp_sum / count ;
   var avg1 = avg? avg.toFixed(2): "Not Available";


    return {
        state: 'success',
        output: {
            "historical_velocity": avg1
        }
    };
}

