function handleNode(context) {
  var root_ous = JSON.parse(context.getParam("project_ous"));
  var projects = [];
  var projectsRootOU = {};

  root_ous.forEach(function(entry) {
    var str = entry.name.split(" ");
    projects.push(str[1]);
    projectsRootOU[str[1]] = entry.id;
  });

  var prog_increments = JSON.parse(JSON.stringify(levelops.tables().query("PI Planned start and end dates", { "PI": "" }, "contains")));

  //var prog_increments = levelops.tables().distinct("PI Planned start and end dates", "PI");
  var prog_increments_arr = [];
  prog_increments.forEach(function(entry){

    var start1 = parseInt(Date.parse(new Date(entry.start))/1000);
    var start2 = parseInt(Date.parse(new Date(entry.end))/1000);
    var end1 = parseInt(Date.parse(new Date(entry.end))/1000);
    var end2 = parseInt(Date.parse(new Date(entry.end))/1000) + 86400;
    var pi = {};
    pi.key = entry.pi;
    pi.start1 = start1;
    pi.start2 = start2;
    pi.end1 = end1;
    pi.end2 = end2;
    prog_increments_arr.push(pi);
  });



  return { 
    state : 'success',
    output : { 
      'projects' : projects,
      'projectsRootOU': projectsRootOU,
      'start_ts' : parseInt(Date.now()/1000 - (90*86400)),
      'end_ts' : parseInt(Date.now()/1000  + (90*86400)),
      "product_id": context.getParam('product_id'),
      "integration_ids": JSON.parse(context.getParam('integration_ids')),
      'ou_category_id': context.getParam('ou_category_id'),
      prog_increments_arr: prog_increments_arr,
      prog_increments_arrs: JSON.stringify(prog_increments_arr)
    } 
  };
}

