function handleNode(context) {
  var records = JSON.parse(context.getParam('existingPI'));
  var parentNodes = JSON.parse(context.getParam('parent_nodes'));
  var project = context.getParam('project');
  var features = JSON.parse(context.getParam('features'));
  //var pi_map = JSON.parse(context.getParam('pi_map'));
  var pi = context.getParam('pi');
  var pi_ou;
  if(records.length > 0 ) {
    pi_ou = records[0];
    if(pi_ou.sections && pi_ou.sections.integrations && pi_ou.sections.integrations["3"]) {
      var custom_field = Object.keys(pi_ou.sections.integrations["3"].custom_fields)[0];
      pi_ou.sections.integrations["3"].custom_fields[custom_field] = features;  
    }
  }

  // levelops.serviceName().doSomething() - invoke a levelops service
  return { 
    state : 'success',
    output : { 
      'present' : records.length > 0,
      'parentNode': parentNodes[project],
      'pi_ou_update' : [pi_ou],
      'recordss': JSON.stringify(records)
    } 
  };
}

