function handleNode(context) {
  var org_unit = JSON.parse(context.getParam('org_unit'));
  var workflow_profile = JSON.parse(context.getParam('workflow_profile'));
  var lead_time_stages = JSON.parse(context.getParam('lead_time_stages'));
  var tags = JSON.parse(context.getParam('tags'));

  var message = "";
  var comparison = {};
  var sendmail = false;
  var violations = [];



  var addTableRow = function (vals, isHeader = false) {
    var rowHtml = '<tr>';
    vals.forEach(val => {
      rowHtml += (isHeader ? '<th>' : '<td>') + val + (isHeader ? '</th>' : '</td>');
    });
    rowHtml += '</tr>';
    message += rowHtml;
  };


  if (workflow_profile) {
  	workflow_profile.fixed_stages.forEach(function(stage, index){
      comparison[stage.name] = {};
      var stage_lower_limit = stage.lower_limit_value;
       var stage_upper_limit = stage.upper_limit_value;
      if (stage.lower_limit_unit === "DAYS") {
      	stage_lower_limit = parseInt(stage.lower_limit_value) * 86400;
      	stage_upper_limit = parseInt(stage.upper_limit_value) * 86400;        
      }
      var ideal_diff = parseInt(stage_lower_limit) - parseInt(lead_time_stages[index].mean);
      var acceptable_diff = parseInt(stage_upper_limit) - parseInt(lead_time_stages[index].mean);

      if (ideal_diff < 0) {
      	violations.push(stage.name);
      }
      comparison[stage.name].mean = (parseInt(lead_time_stages[index].mean)/86400).toFixed(2);
      comparison[stage.name].ideal_diff = (ideal_diff/86400).toFixed(2);
      comparison[stage.name].ideal = (stage_lower_limit/86400).toFixed(0);
      comparison[stage.name].acceptable_diff = (acceptable_diff/86400).toFixed(2);
      comparison[stage.name].acceptable = (stage_upper_limit/86400).toFixed(0);
    });

  }

  Object.keys(comparison).forEach(function(key){    
     if ((comparison[key].acceptable_diff < 0) || (comparison[key].ideal_diff < 0)) {
       message+= `
`;
       message+= "<p>"+ key +"</p>";
       message += '<table border=1 cellpadding=5 style="border-collapse: collapse; ">';
       addTableRow(["Average Time", "Ideal Time", "Acceptable Time"], true);
       var mean = comparison[key].mean === "1"? comparison[key].mean + " day" : comparison[key].mean + " days";
       var ideal = comparison[key].ideal === "1"? comparison[key].ideal + " day" : comparison[key].ideal + " days";
       var acceptable = comparison[key].acceptable === "1"? comparison[key].acceptable + " day" : comparison[key].acceptable + " days";       
       addTableRow([mean , ideal, acceptable]);
       message+= "</table>";
     }     
  });


  return { 
    state : 'success',
    output : { 
      'comparison' : JSON.stringify(comparison),
      'message': message,
      'sendMail': message.length > 0
    } 
  };
}

