prefix = 'hello ';
function handleNode(context) {

    var workflow_profiles = JSON.parse(context.getParam('workflow_profiles'));
  var tags = JSON.parse(context.getParam('tags'));

  var workflow_profiles_obj = {};
  var default_workflow_profile = {};

  var lead_time_profile = "";
  tags.forEach(function(tag) {
  	if(tag.name.includes("lead_time")) {
    	lead_time_profile = tag.name.split(":")[1];
    }
  });

  workflow_profiles.forEach(function(profile){
  	workflow_profiles_obj[profile.name.toLowerCase()] = profile;
    if(lead_time_profile === "" && profile.default_config) {
      default_workflow_profile = profile;
    }
  });

  var workflow_profile = lead_time_profile.length === 0 ?  default_workflow_profile : workflow_profiles_obj[lead_time_profile.toLowerCase()];
  return { 
    state : 'success',
    output : { 
      'lead_time_profile' : lead_time_profile,
      //'isValid': lead_time_profile.length > 0,
      'isValid': true,
      'workflow_profile': workflow_profile,
      workflow_profiles_obj: workflow_profiles_obj
    } 
  };
}

