function handleNode(context) {
  let prs  = JSON.parse(context.getParam('prs'));
  let large_prs  = JSON.parse(context.getParam('large_prs'));
  prs = prs.filter(item => !item.creator.includes("bot"));
  prs = prs.slice(0, 20);

  var tags = JSON.parse(context.getParam('tags'));

  const ou  = JSON.parse(context.getParam('ou'));
  //const threshold  = JSON.parse(context.getParam('threshold'));
  const now = parseInt(Date.now()/1000);
  var config = JSON.parse(context.getParam("config"));
  const threshold = parseInt(config.pr_sla_days);
  var shallow_comment_count = parseInt(config.shallow_comment_count);
  const dayss = 86400*threshold;

  var sla_message_header = "<p><b>PRs Open for > "+ threshold +" Days</b><p>";
  var sla_message = "";

  var jira_missing_message_header = "<p><b>PRs without Jira Linkage</b><p>";
  var jira_missing_message = "";

  var prs_without_reviews_message_hdr = "<p><b>PRs without review comments</b><p>";
  var prs_without_reviews_message = "";

  var large_PRs_message_hdr = "<p><b>Large PRs</b><p>";
  var large_PRs_message = "";

  prs.forEach(
    record => {
      let url = "https://github.com/" + record.repo_id[0] + "/pull/" + record.number;
      let openDays = ((Date.now()/1000 - record.pr_created_at)/86400);
      // SLA message
      if (now - record.pr_created_at > dayss) {
        let updatedOn = (new Date(record.pr_updated_at * 1000)).toUTCString();
        if (openDays < 14) {
          sla_message +="<p><a href="+url+">"+ "PR #"+record.number +"</a> - " +record.creator + " - " + record.repo_id + " - " + record.title +"</p>";
          sla_message +="<p>Last Updated: "+updatedOn+" - Open > " +openDays.toFixed(1)+" days </p>";
        }
      }

      // Jira linkage missing
      if (!record.has_issue_keys) {
        let updatedOn = (new Date(record.pr_updated_at * 1000)).toUTCString();
        jira_missing_message +="<p><a href="+url+">"+ "PR #"+record.number +"</a> - " +record.creator + " - " + record.repo_id + " - " + record.title +"</p>";
        jira_missing_message +="<p>Last Updated: "+updatedOn+" - Open > " +openDays.toFixed(1)+" days </p>";
      }

      // PRs without review comments
      if (record.comment_density === "shallow") {
        prs_without_reviews_message +="<p><a href="+url+">"+ "PR #"+record.number +"</a> - " +record.creator + " - " + record.repo_id + " - " + record.title +"</p>";
      }
    }
  );

  //SLACK
  var slackMessagePROpen = "";
  var slackMessageJiraLink = "";
  var slackMessagePRWithoutReviews = "";
  var slackMessageLargePRs = "";
  var messageBody = '';
  var seconds = Math.round(new Date().getTime() / 1000);
  var reminders = [];

  if(prs.length > 0) {
    slackMessagePROpen += "PRs Open for more than "+threshold+" days - "+ou.name+" \n";
    prs.forEach(
      record => {
        var url = "https://github.com/" + record.repo_id[0] + "/pull/" + record.number;
        if((seconds - record.pr_created_at  > 86400*threshold) && (record.state === "open")) {
          reminders.push(record);
          var updatedOn = (new Date(record.pr_updated_at * 1000)).toUTCString();
          var openDays = ((Date.now()/1000 - record.pr_created_at)/86400);

          if(openDays < 14) {
            slackMessagePROpen+= "<"+url+"|PR #"+record.number+"> - ["+record.creator+"] - "+record.repo_id[0]+" - "+record.title + " \n";
            slackMessagePROpen+= "Last Updated: "+updatedOn+" - Open for "+openDays.toFixed(1)+" days \n \n";
          }
        }

        // missing JIRA keys
        if (!record.has_issue_keys) {
          slackMessageJiraLink+= "<"+url+"|PR #"+record.number+"> - ["+record.creator+"] - "+record.repo_id[0]+" - "+record.title + " \n";
        }

        // PRs without review comments
        if (record.comment_count <= shallow_comment_count) {
          slackMessagePRWithoutReviews+= "<"+url+"|PR #"+record.number+"> - ["+record.creator+"] - "+record.repo_id[0]+" - "+record.title + " \n";
        }

        // Large PRs message
        if(record.code_change === "large") {
          slackMessageLargePRs += "<"+url+"|PR #"+record.number+"> - ["+record.creator+"] - "+record.repo_id[0]+" - "+record.title + " \n";
        }
      }
    );
  }

  large_prs.forEach(record => {
    // Large PRs message
    var url = "https://github.com/" + record.repo_id[0] + "/pull/" + record.number;
    large_PRs_message += "<p><a href="+url+">"+ "PR #"+record.number +"</a> - " +record.creator + " - " + record.repo_id + " - " + record.title +"</p>";
    slackMessageLargePRs += "<"+url+"|PR #"+record.number+"> - ["+record.creator+"] - "+record.repo_id[0]+" - "+record.title + " \n";
  });

  var message = "";
  if(sla_message.length > 0) {
    message += sla_message_header + sla_message;
  }
  if(jira_missing_message.length > 0) {
    message += jira_missing_message_header + jira_missing_message;
  }
  if(prs_without_reviews_message.length > 0) {
    message += prs_without_reviews_message_hdr + prs_without_reviews_message;
  }
  if(large_PRs_message.length > 0) {
    message += large_PRs_message_hdr + large_PRs_message;
  }

  var mail_ids = [], channel_ids = [];
  if(ou.tags.length > 0) {
    tags.forEach(function(tag) {
      var tag_split = tag.name.split(":");
      if(tag_split[0].toLowerCase() === "mailto") {
        mail_ids.push(tag_split[1]);
      } else if (tag_split[0].toLowerCase() === "channelid"){
        channel_ids.push(tag_split[1]);
      }
    });
  }


  return { 
    state : 'success',
    output : { 
      'message' : message,
      'slackMessages': [slackMessagePROpen, slackMessageJiraLink, slackMessagePRWithoutReviews, slackMessageLargePRs],
      'showSlackMessages': [config.showPRviolatingSLA, config.showPRwithoutJira, config.showPRwithoutComments, config.showLargePRs],
      'mail_ids': mail_ids,
      'channel_ids': channel_ids
    } 
  };
}

