/* jshint  esversion: 10 */
function handleNode(context) {
  const all_stories = JSON.parse(context.getParam('all_stories'));
  const completed_stories = JSON.parse(context.getParam('completed_stories'));
  const ou = JSON.parse(context.getParam('ou'));

  const completed_stories_sum = completed_stories[0].workitem_created_at.records.reduce((sum, next) => sum + next.total_story_points, 0);
  const all_stories_sum = all_stories[0].workitem_created_at.records.reduce((sum, next) => sum + next.total_story_points, 0);
  const remaining = all_stories_sum  - completed_stories_sum;


  const row = {
  	"0": ou.id,
    "1": getFormattedDate(new Date()),
    "2": completed_stories_sum,
    "3": remaining,
    "4": all_stories_sum
  };
  // levelops.serviceName().doSomething() - invoke a levelops service
  return { 
    state : 'success',
    output : { 
      'row' : row  
    } 
  };
}

function getFormattedDate(date) {
  var year = date.getFullYear();
  var month = (1 + date.getMonth()).toString();
  month = month.length > 1 ? month : '0' + month;
  var day = date.getDate().toString();
  day = day.length > 1 ? day : '0' + day; 
  return month + '/' + day + '/' + year;
}

