prefix = 'hello ';
function handleNode(context) {
  var sprints = JSON.parse(context.getParam('sprints'));
  var latest_sprints = JSON.parse(context.getParam('latest_sprints'));

  latest_sprints.sort(function(a,b){
   return b.key - a.key;  
  });

//  var latest_sprint_time = latest_sprints.length > 0? latest_sprints[0].key : Date.now()/1000;
  var latest_sprint_time = Date.now()/1000;
  // levelops.serviceName().doSomething() - invoke a levelops service

  var sprint_key = latest_sprints[0].additional_key.toString().slice(0,22);

  var future_sprints = sprints.filter(function(sprint){  
  	return (sprint.start_date >= latest_sprint_time*1000) && (sprint_key.toString() === sprint.parent_sprint.toString().slice(0,22));
  });

  future_sprints.sort(function(a, b){
    return a.start_date - b.start_date;
  });

  var two_future_sprints = [];
  var temp = future_sprints.slice(0,6);
  temp.forEach(function(sprint){
  	sprint.sprint_full_name = sprint.parent_sprint + "\\" + sprint.name;
    two_future_sprints.push(sprint);  
  });

  return { 
    state : 'success',
    output : { 
      'future_sprints' :[JSON.stringify(JSON.stringify(two_future_sprints[0].sprint_full_name)).slice(3,-3).split("\\\\").join("\\"), 
                        JSON.stringify(JSON.stringify(two_future_sprints[1].sprint_full_name)).slice(3,-3).split("\\\\").join("\\")
                        ],
      'future_sprintss' : JSON.stringify(two_future_sprints)
    } 
  };
}

