function handleNode(context) {
  var tags = JSON.parse(context.getParam('tags'));
  // levelops.serviceName().doSomething() - invoke a levelops service
  var emails = [];

  tags.forEach(function(tag){
    if(tag.name.toLowerCase().includes("mailto")) {
      var mail = tag.name.toString().split(":");
      var mailid = mail.length && mail[1] && (mail[1].length > 0) > 0? mail[1]: ""; 
      emails.push(mailid);
    }
  });

  return { 
    state : 'success',
    output : { 
      'emails' : emails
    } 
  };
}

