prefix = 'hello ';
function handleNode(context) {
  var issues = JSON.parse(context.getParam('issues'));
    var org_unit = JSON.parse(context.getParam('org_unit'));
  var threshold = context.getParam('threshold');
  var time = threshold * 86400;
  // levelops.serviceName().doSomething() - invoke a levelops service

 var message = ""; 

  var addTableRow = function (vals, isHeader = false) {
    var rowHtml = '<tr>';
    vals.forEach(val => {
      rowHtml += (isHeader ? '<th>' : '<td>') + val + (isHeader ? '</th>' : '</td>');
    });
    rowHtml += '</tr>';
    message += rowHtml;
  };
  var base_url = "https://dev.azure.com/eysbp/eysbp/CTP - Core Platform/_workitems/edit/";
  var hasRows = true;
  issues.forEach(function(issue){
    var active = issue.status_list.filter(function(status){
      return status.field_value === "Active";
    });
    var active_time = active[0] && active[0].end_date - active[0].start_date > 0 ? active[0].end_date - active[0].start_date: 0;
  	if(active_time >= (6*86400000) && active_time < (15*86400000)) {

      var url = "<a href='"+ base_url+issue.workitem_id +"'>"+ issue.workitem_id + "</a>";
      if(hasRows) {
        message += '<h3>User Stories - Active > 6 days</h3>';
  		message += '<table border=1 cellpadding=5 style="border-collapse: collapse; ">';
  		addTableRow(["Id", "Title", "Active Days", "Assignee", "Labels"], true);
        hasRows = false;
      }
      addTableRow([url, issue.summary, (active_time/86400000).toFixed(1) + " days", issue.assignee, issue.labels.toString()]);
    }
  });
  message+= "</table>";

  hasRows = true;
  issues.forEach(function(issue){
    var active = issue.status_list.filter(function(status){
      return status.field_value === "Active";
    });
    var active_time = active[0] && active[0].end_date - active[0].start_date > 0 ? active[0].end_date - active[0].start_date: 0;
  	if(active_time >= (15*86400000) && active_time < (30*86400000)) {

      var url = "<a href='"+ base_url+issue.workitem_id +"'>"+ issue.workitem_id + "</a>";
      if(hasRows) {
        message += '<h3>User Stories - Active > 15 days</h3>';
  		message += '<table border=1 cellpadding=5 style="border-collapse: collapse; width:100%;">';
  		addTableRow(["Id", "Title", "Active Days", "Assignee", "Labels"], true);
        hasRows = false;
      }
      addTableRow([url, issue.summary, (active_time/86400000).toFixed(1) + " days", issue.assignee, issue.labels.toString()]);
    }
  });
  message+= "</table>";

  hasRows = true;
  issues.forEach(function(issue){
    var active = issue.status_list.filter(function(status){
      return status.field_value === "Active";
    });
    var active_time = active[0] && active[0].end_date - active[0].start_date > 0 ? active[0].end_date - active[0].start_date: 0;
  	if(active_time >= (30*86400000)) {
      var url = "<a href='"+ base_url+issue.workitem_id +"'>"+ issue.workitem_id + "</a>";
      if(hasRows) {
        message += '<h3>User Stories - Active > 30 days</h3>';
		message += '<table border=1 cellpadding=5 style="border-collapse: collapse; width:100%;">';
  		addTableRow(["Id", "Title", "Active Days", "Assignee", "Labels"], true);
        hasRows = false;
      }
      addTableRow([url, issue.summary, (active_time/86400000).toFixed(1) + " days", issue.assignee, issue.labels.toString()]);
    }
  });
  message+= "</table>";

  message+= "<p>Please click <a href=" + "https://eu1.app.propelo.ai/#/admin/dashboards/9?OU="+ org_unit.id +"&workspace_id=2" + " target='_blank'>here</a> for more details. </p>";
  return { 
    state : 'success',
    output : { 
      'message' :message
    } 
  };
}

