function handleNode(context) {
  var org_unit = JSON.parse(context.getParam('org_unit'));
  var no_assignee = JSON.parse(context.getParam('no_assignee'));
  var poor_desc = JSON.parse(context.getParam('poor_desc'));
  var missing_ac = JSON.parse(context.getParam('missing_ac'));
  var sp_missing = JSON.parse(context.getParam('sp_missing'));
  var orphan_stories = JSON.parse(context.getParam('orphan_stories'));
  // var tags = JSON.parse(context.getParam('org_unit_tags'));
  // levelops.serviceName().doSomething() - invoke a levelops service

  var email_message = "";
  var hasHygieneMiss = false;
  var base_url = "https://dev.azure.com/eysbp/CTP - Core Platform/_workitems/edit/";


  var addTableRow = function (vals, isHeader) {
    var rowHtml = '<tr>';
    isHeader = isHeader || false;
    vals.forEach(function(val) {
      rowHtml += (isHeader ? '<th>' : '<td>') + val + (isHeader ? '</th>' : '</td>');
    });
    rowHtml += '</tr>';
    email_message += rowHtml;
  };

  if((no_assignee.length + poor_desc.length + missing_ac.length + sp_missing.length + orphan_stories.length) > 0) {
    email_message = "<h3>Hygiene Misses</h3>";
  }


  if(no_assignee && no_assignee.length > 0) {
    email_message += "<h4>No Assignee</h4>";
    email_message += '<table border=1 cellpadding=5 style="border-collapse: collapse; width:100%; ">';
    addTableRow(["Workitem", "Summary", "Assignee", "Labels"], true);
    no_assignee.forEach(function(entry) {
      addTableRow(["<a href='"+ base_url+entry.workitem_id +"'>"+ entry.workitem_id +"</a>", entry.summary, entry.assignee, entry.labels.toString()]); 
    });
    email_message += "</table>";
  }

  if(poor_desc && poor_desc.length > 0) {
    email_message += "<h4>Poor Description</h4>";
    email_message += '<table border=1 cellpadding=5 style="border-collapse: collapse; width:100%;">';
    addTableRow(["Workitem", "Summary", "Assignee", "Labels"], true);
    poor_desc.forEach(function(entry) {
      addTableRow(["<a href='"+ base_url+entry.workitem_id +"'>"+ entry.workitem_id +"</a>", entry.summary, entry.assignee, entry.labels.toString()]); 
    });
    email_message += "</table>";
  }

  if(missing_ac.length > 0) {
    email_message += "<h4>Missing acceptance criteria</h4>";
    email_message += '<table border=1 cellpadding=5 style="border-collapse: collapse; width:100%;">';
    addTableRow(["Workitem", "Summary", "Assignee", "Labels"], true);    
    missing_ac.forEach(function(entry) {
      addTableRow(["<a href='"+ base_url+entry.workitem_id +"'>"+ entry.workitem_id +"</a>", entry.summary, entry.assignee, entry.labels.toString()]); 
    });
    email_message += "</table>";
  }

  if(sp_missing && sp_missing.length > 0) {
    email_message += "<h4>Story Points Missing</h4>";
    email_message += '<table border=1 cellpadding=5 style="border-collapse: collapse; width:100%; ">';
    addTableRow(["Workitem", "Summary", "Assignee", "Labels"], true);    
    sp_missing.forEach(function(entry) {
      addTableRow(["<a href='"+ base_url+entry.workitem_id +"'>"+ entry.workitem_id +"</a>", entry.summary, entry.assignee, entry.labels.toString()]);  
    });
    email_message += "</table>";    
  }

  if(orphan_stories && orphan_stories.length > 0) {
    email_message += "<h4>Orphan Stories</h4>";
    email_message += '<table border=1 cellpadding=5 style="border-collapse: collapse; width:100%;">';
    addTableRow(["Workitem", "Summary", "Assignee", "Labels"], true);    
    orphan_stories.forEach(function(entry) {
      addTableRow(["<a href='"+ base_url+entry.workitem_id +"'>"+ entry.workitem_id +"</a>", entry.summary, entry.assignee, entry.labels.toString()]);   
    });
    email_message += "</table>";    
  }

  if((no_assignee.length + poor_desc.length + missing_ac.length + sp_missing.length + orphan_stories.length) > 0) {
    email_message+= "<p>Please click <a href=" + "https://eu1.app.propelo.ai/#/admin/dashboards/9?OU="+ org_unit.id +"&workspace_id=2" + " target='_blank'>here</a> for more details. </p>";
  }



  return { 
    state : 'success',
    output : { 
      'message' : email_message,
      'hasMessage': email_message.trim().length > 0
    } 
  };
}

