function handleNode(context) {
  const row = JSON.parse(context.getParam("job"));
   const jenkins_uuid = context.getParam("jenkins_uuid");
    var commits = JSON.parse(context.getParam("commits"));
  const job_full_name =  row.name + "--" + row.display_title  ;
  var date = new Date(row.run_started_at);
  var created_at = new Date(row.created_at);
  var updated_at = new Date(row.updated_at);

  var duration = updated_at.getTime() - created_at.getTime();

  commits = !commits.message ? commits.map(item => item.sha) : [row.head_commit.id];
  var start_time = date.getTime();
  const payload = {
    job_name: row.name,
    user_id: "SCMTrigger",
    job_run_params: [
      {
        type: "StringParameterValue",
        name: "version",
        value: row.run_number
      },
      {
        type: "StringParameterValue",
        name: "revision",
        value: row.run_number
      }
    ],
    repo_url: row.repository.owner.url,
    start_time: start_time,
    result: row.conclusion,
    duration: duration,
    build_number: row.id,
    jenkins_instance_guid: jenkins_uuid,
    jenkins_instance_name: "Jenkins Instance",
    jenkins_instance_url: "https://jenkins.dev.levelops.io/",
    job_run: null,
    job_full_name: job_full_name,
    job_normalized_full_name: job_full_name,
    branch_name: row.head_branch,
    module_name: null,
    scm_commit_ids: commits,
    trigger_chain: [{
      id: "SCMTrigger",
      type: "SCMTriggerCause"
    }]
  };
  // var strp = JSON.stringify(payload).replace(/"/g, '\\"');
  // levelops.serviceName().doSomething() - invoke a levelops service
  return { 
    state : "success",
    output : { 
      "cicd" : {
        request_type: "JenkinsPluginJobRunComplete",
        payload: JSON.stringify(payload)
      }
    } 
  };
}

