function handleNode(context) {
  var ous = JSON.parse(context.getParam('ous'));
  var repos = [];
  ous.forEach(function(ou) {
    if(ou.sections && ou.sections.length > 0) {
      ou.sections.forEach(function(section) {
        var integrations = section.integrations;
        Object.keys(integrations).forEach(function(integration_id) {
          //console.log(integrations[integration_id]);
          if(integrations[integration_id] && integrations[integration_id].type === "github" && integrations[integration_id].filters &&  integrations[integration_id].filters.repo_ids && integrations[integration_id].filters.repo_ids.length > 0) {
            repos = repos.concat(integrations[integration_id].filters.repo_ids);
          }
        });
      });
    }
  });

  var repos_unique= [];
  repos.forEach(function(repo, index) {
    if(repos.indexOf(repo) === index) {
      repos_unique.push(repo);
    }
  });

  // propels.serviceName().doSomething() - invoke a service
  return { 
    state : 'success',
    output : { 
      'repos' : repos_unique
    } 
  };
}

