prefix = 'hello ';
function handleNode(context) {
  var epicStoryDetails = JSON.parse(context.getParam('epicDetails')).records;
  var epicmetadata = JSON.parse(context.getParam('epic_metadata'));
  var estimate_field = context.getParam('estimate_field');
  var startDate_field = context.getParam('startDate_field');
  // levelops.serviceName().doSomething() - invoke a levelops service
  var story_open = [];
  var sp_completed = 0; // story open/completed, no story points

  epicStoryDetails.forEach(function (task) {
    if (task.status && task.status === "CLOSED") {
      var status = task.status_list.filter(sts => sts.status === "CLOSED");
      if (status.length > 0) {
        sp_completed += 1;
      }
    } else {
      story_open.push({
        "0": task.key,
        "1": task.summary
      });
    }

  });

  const estimate = parseInt(epicmetadata.custom_fields[estimate_field] || "0");
  var startTime = (epicmetadata.custom_fields && epicmetadata.custom_fields[startDate_field]) ? (epicmetadata.custom_fields[startDate_field] / 1000).toFixed() : epicmetadata.issue_created_at;
  var due_date = new Date((parseInt(startTime) + estimate * 604800) * 1000).toLocaleDateString();

  var percent_completed = sp_completed > 0 ? ((sp_completed / (epicStoryDetails.length)) * 100).toFixed(0) : 0;
  var epic_due_date = Date.parse(due_date) / 1000;
  var today = Date.now() / 1000;
  var days_remaining = epic_due_date > today ? (epic_due_date - today) / 86400 : 0;
  var weeks_remaining = (days_remaining / 7).toFixed(1);
  var color = "Green";
  var week_rem = Math.floor(weeks_remaining);
  if (estimate > 0 && weeks_remaining < estimate / 2) {
    switch (week_rem) {
      case 0:
        if (percent_completed < 100) {
          color = "Red";
        }
        break;
      case 1:
        if (percent_completed <= 60) {
          color = "Red";
        }
        break;
      case 2:
        if (percent_completed >= 40 && percent_completed < 60) {
          color = "Yellow";
        } else if (percent_completed < 40) {
          color = "Red";
        }
        break;
      case 3:
        if (percent_completed < 40) {
          color = "Red";
        }
        break;
      default:
    }
  }

  var epicJIRAStatus = "OPEN";
  epicmetadata.status_list.forEach(function (status) {
    if (status.status === "CLOSED" || status.status === "DONE") {
      epicJIRAStatus = status.status;
    }
  });

  var half_estimate = Math.floor(estimate / 2);

  var sendMail = false;
  if (color === 'Green' && (week_rem === half_estimate || week_rem === 1)) {
    sendMail = true;
  } else if ((color === 'Red' || color === 'Yellow') && (week_rem === half_estimate || week_rem < 3)) {
    sendMail = true;
  }

  var message = "<div>";

  var addTableRow = function (vals, isHeader = false) {
    var rowHtml = '<tr>';
    vals.forEach(val => {
      rowHtml += (isHeader ? '<th>' : '<td>') + val + (isHeader ? '</th>' : '</td>');
    });
    rowHtml += '</tr>';
    message += rowHtml;
  };
  message += "<h3>" + epicmetadata.key + ": " + epicmetadata.summary + "</h3>";

  message += "<p>Current Progress</p>";
  message += '<table border=1 cellpadding=5 style="border-collapse: collapse; ">';

  addTableRow(["Percent Completed", percent_completed]);
  addTableRow(["Stories Completed", sp_completed + "/" + epicStoryDetails.length]);
  addTableRow(["Stories Open", (epicStoryDetails.length - sp_completed) + "/" + epicStoryDetails.length]);
  addTableRow(["Weeks Estimated", estimate]);
  addTableRow(["Due Date", due_date]);
  addTableRow(["Current Status", epicJIRAStatus]);
  addTableRow(["Weeks Remaining", weeks_remaining]);
  addTableRow(["Projected SLA Status", color]);

  message += "</table>";
  message += "<br>";
  if (story_open.length > 0) {
    message += "<h4>Stories Open</h4>";
    story_open.forEach(function (story) {
      message += "<p>" + story["0"] + ": " + story["1"] + "</p>";
    });
  } else {
    message += "<p>No OPEN Stories</p>";
  }
  message += "<br>";
  var note = `How is above status calculated?
- Once the middle of epic progress is arrived, the percent completed is calculated.
- The percentage completion uses story completed as the basis of calculation.
- Completion between 40% - 60% (2 weeks remaining) - Yellow
- Completion less than 40% (less than 3 weeks remaining), Completion below 100% (less than one week remaining) - Red
- Otherwise it's marked Green
`;

  message += note;
  message += "</div>";
  //if(sendMail) {
  //  levelops.emails().send("EPIC SLA Tracker:" + epicmetadata.epic, message, "shashank@propelo.ai");
  //}

  return {
    state: 'success',
    output: {
      'subject': "EPIC SLA Tracker:" + epicmetadata.key,
      'message': message
    }
  };

}

