function handleNode(context) {
  var ou = JSON.parse(context.getParam('ou'));
  var tags = JSON.parse(context.getParam('tags')).records;
  var tagsMap = {};
  var emails = [];
  tags.forEach(function(tag){
    tagsMap[tag.id] = tag.name;
  });

  ou.managers.forEach(function(manager){
    if(emails.indexOf(manager.email) < 0) {
      emails.push(manager.email);
    }
  });

  ou.tags.forEach(function(tagid){
    if(emails.indexOf(tagsMap[tagid]) < 0 && tagsMap[tagid].indexOf('@') > 0) {
      emails.push(tagsMap[tagid]);
    }
  });

  // levelops.serviceName().doSomething() - invoke a levelops service
  return { 
    state : 'success',
    output : { 
      emails: emails 
    } 
  };
}

