function handleNode(context) {
  const intgrationStatus = JSON.parse(context.getParam('status'));
   logs = JSON.parse(context.getParam('log'));
  //const logs = JSON.parse(context.getParam('logs'));
  const intgrationName = context.getParam('name');
  const integration_type = context.getParam('type');

 var stateHash='';

var integration_id = intgrationStatus.integration_id;
var intStatus = intgrationStatus.status;
var integration_name = context.getParam('name');

var integration_message = "";
var integration_stacktrace = "";

 if(intStatus == 'failed'){
    var message = logs.records[0].result.message;
    var stackTrace = logs.records[0].result.stacktrace[0];

    var stackTraceList = logs.records[0].result.stacktrace;
  // var st = "";
    var keys = "";

    for( i=0; i<stackTraceList.length; i++){
      keys = keys + Object.keys(stackTraceList[i]);
      keys = keys + "\n<br/>";
    }

      integration_message = message;
      integration_stacktrace = keys;
 }else{
     integration_message = "";
     integration_stacktrace = "";
 }

