function handleNode(context) {
  var sprint_stories = JSON.parse(context.getParam("sprint_stories"));
  var sprint = JSON.parse(context.getParam("sprint"));
  var unplanned_stories = [];
  var statuses = ["TODO", "IN PROGRESS"];
  sprint_stories.forEach(function(story){
    var status_list = story.status_list;
    var updated = false;
    status_list.forEach(function(status){
      if(statuses.indexOf(status.status) >=0 && parseInt(sprint.key) < status.start_time && !updated){
        unplanned_stories.push(story);
      }
    });
  });

  // levelops.serviceName().doSomething() - invoke a levelops service
  return { 
    state : 'success',
    output : { 
      'unplanned_stories' : unplanned_stories
    } 
  };
}

