function handleNode(context) {
    var sprint = JSON.parse(context.getParam('sprint'));
    var ou = JSON.parse(context.getParam('ou'));
    var sprintMetrics = JSON.parse(context.getParam('sprintMetrics'));
    var hygieneAssignee = JSON.parse(context.getParam('hygieneAssignee'));
    var hygieneStoryPoints = JSON.parse(context.getParam('hygieneStoryPoints'));
    var hygieneMissingAC = JSON.parse(context.getParam('hygieneMissingAC'));
    var hygienePoorDesc = JSON.parse(context.getParam('hygienePoorDesc'));
    var leadTime = JSON.parse(context.getParam('leadTime'));
    var issueStoryPtBD = JSON.parse(context.getParam('issueStoryPtBD'));
    //var committedStoriesCount = JSON.parse(context.getParam('committedStoriesCount'));
    var unplanned_stories = JSON.parse(context.getParam('unplanned_stories'));

    var html_message = "<div style='border: 1px solid; padding: 24px'>";
    html_message += '<h1>Sprint Report for ' + ou.name + '</h1>';
    html_message += '<h2>Sprint: ' + sprint.additional_key + '</h2>';
    html_message += '<h3>Sprint Status</h3>';
    html_message += '<table border=1 cellpadding=5 style="border-collapse: collapse; ">';

    var text_message = `
Sprint Report for ${ou.name}
Sprint: ${sprint.additional_key}
    `;

    var addTableRow = function (vals, isHeader = false) {
        var rowHtml = '<tr>';
        vals.forEach(val => {
            rowHtml += (isHeader ? '<th>' : '<td>') + val + (isHeader ? '</th>' : '</td>');
        });
        rowHtml += '</tr>';
        html_message += rowHtml;
    };
    var committedStoriesCountNum = sprint.committed_keys ? sprint.committed_keys.length : '-';
    var headers = ['Committed # of Keys', 'Committed Story Points', 'Delivered Story Points', 'Commit to Done', 'Creep Story Points', 'Delivered Creep Story Points'];
    var commitToDonePc = ((((sprint.delivered_story_points / sprint.committed_story_points)) * 100).toFixed(2) + "%");
    var vals = [committedStoriesCountNum, sprint.committed_story_points, sprint.delivered_story_points, commitToDonePc, sprint.creep_story_points, sprint.delivered_creep_story_points];

    addTableRow(headers, true);
    addTableRow(vals);
    html_message += '</table>';

    text_message += `
Sprint Status    
Committed # of Keys: ${committedStoriesCountNum}
Committed Story Points: ${sprint.committed_story_points}
Delivered Story Points: ${sprint.delivered_story_points}
Commit to Done: ${commitToDonePc}
Creep Story Points: ${sprint.creep_story_points} 
Delivered Creep Story Points: ${sprint.delivered_creep_story_points}
`;

    html_message += '<h3>Hygiene Misses</h3>';
    html_message += '<table border=1 cellpadding=5 style="border-collapse: collapse; ">';
    addTableRow(['Tickets without Assignee', 'Stories/Bugs without Story Points', 'Missing Acceptance Criteria', 'Has Poor Description'], true);
    addTableRow([(hygieneAssignee.length ? hygieneAssignee[0].total_tickets : 0),
    (hygieneStoryPoints.length ? hygieneStoryPoints[0].total_tickets : 0),
    (hygieneMissingAC.length ? hygieneMissingAC[0].total_tickets : 0),
    (hygienePoorDesc.length ? hygienePoorDesc[0].total_tickets : 0)]);
    html_message += '</table>';

    text_message += `
Hygiene Misses
Tickets without Assignee: ${hygieneAssignee.length ? hygieneAssignee[0].total_tickets : 0}
Stories/Bugs without Story Points: ${hygieneStoryPoints.length ? hygieneStoryPoints[0].total_tickets : 0}
Missing Acceptance Criteria: ${hygieneMissingAC.length ? hygieneMissingAC[0].total_tickets : 0}
Has Poor Description: ${hygienePoorDesc.length ? hygienePoorDesc[0].total_tickets : 0}
`;

    html_message += '<h3>Story Point Breakdown</h3>';
    html_message += '<table border=1 cellpadding=5 style="border-collapse: collapse; ">';
    text_message += `
Story Point Breakdown`; 
    if (issueStoryPtBD && issueStoryPtBD.length > 0) {
        addTableRow(['Ticket Type', 'Total Tickets', 'Story Points'], true);
        issueStoryPtBD[0].stacks.forEach(type => {
            addTableRow([type.key, type.total_tickets, type.total_story_points]);
            text_message += `
Ticket Type: ${type.key}
Total Tickets: ${type.total_tickets}
Story Points: ${type.total_story_points}
`;
        });
    }
    html_message += '</table>';




    if (leadTime.length > 0) {
        var roundDays = function (secs) {
            return Math.round(secs / 86400);
        };
        var roundDays2 = function (secs) {
            return Math.round(((secs / 86400) + Number.EPSILON) * 100) / 100;
        };
        html_message += '<h3>Lead Time for Stories and Bugs</h3>';
        html_message += '<table border=1 cellpadding=5 style="border-collapse: collapse; ">';
        addTableRow(['', 'Average Time (Days)', 'Median Time (Days)', 'P90 (Days)', 'P95 (Days)'], true);
        var mean = 0, median = 0, p90 = 0, p95 = 0;
        leadTime.forEach(lt => {
            mean += lt.mean;
            median += lt.median;
            p90 += lt.p90;
            p95 += lt.p95;
        });
        addTableRow(['Overall Lead Time', roundDays(mean), roundDays(median), roundDays(p90), roundDays(p95)]);
        html_message += '</table>';

        text_message += `
Lead Time for Stories and Bugs - Overall Lead Time
Average Time (Days): ${roundDays(mean)}
Median Time (Days): ${roundDays(median)}
P90 (Days): ${roundDays(p90)}
P95 (Days): ${roundDays(p95)}
`;

        html_message += '<h3>Lead Time Breakdown</h3>';
        html_message += '<table border=1 cellpadding=5 style="border-collapse: collapse; ">';
        addTableRow(['Stage', 'Average Time (Days)', 'Median Time (Days)', 'P90 (Days)', 'P95 (Days)', 'Rating'], true);
        text_message+= `
Lead Time Breakdown
`;
        leadTime.forEach(lt => {
            addTableRow([lt.key,
            roundDays2(lt.mean),
            roundDays2(lt.median),
            roundDays2(lt.p90),
            roundDays2(lt.p95),
            lt.velocity_stage_result.rating]);
            text_message += `
Stage: ${lt.key}
Average Time: ${roundDays2(lt.mean)} Days  
Median Time: ${roundDays2(lt.median)} Days 
P90: ${roundDays2(lt.p90)} Days
P95: ${roundDays2(lt.p95)} Days 
Rating: ${lt.velocity_stage_result.rating}
`;
        });
        html_message += '</table>';
    }

    if (unplanned_stories.length > 0) {
        html_message += '<h3>Unplanned stories</h3>';
        text_message += `
Unplanned stories
        `;
        html_message += '<table border=1 cellpadding=5 style="border-collapse: collapse; ">';
        addTableRow(['Key', 'Summary'], true);
        unplanned_stories.forEach(function (story) {
            addTableRow([story.key, story.summary]);
            text_message += `
${story.key} - ${story.summary}
            `;
        });

        html_message += '</table>';

    }


    //message+="<br> <p> Click <a href='https://app.levelops.io/#/admin/dashboards/75?OU="+parseInt(ou.id)+"'>here</a> for more details.</p>";
    html_message += "</div>";

    return {
        state: 'success',
        output: {
            'message': text_message
        }
    };
}

